package com.lvl6.mobsters.dynamo;

import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBHashKey;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBRangeKey;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBTable;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBVersionAttribute;

@DynamoDBTable(tableName = "QuestForUser")
public class QuestForUser {

	@DynamoDBVersionAttribute
	private Long version;

	/*
	 * @DynamoDBHashKey(attributeName = "id")
	 * 
	 * @DynamoDBAutoGeneratedKey private String id;
	 */

	// @DynamoDBIndexHashKey(globalSecondaryIndexName = "userIdGlobalIndex")
	@DynamoDBHashKey(attributeName = "userId")
	private String userId;

	// @DynamoDBIndexHashKey(globalSecondaryIndexName = "questIdGlobalIndex")
	@DynamoDBRangeKey(attributeName = "questId")
	private int questId;

	private boolean isRedeemed = false;

	private boolean isComplete = false;

	public QuestForUser() {
	}

	public QuestForUser(String userId, int questId, boolean isRedeemed,
			boolean isComplete) {
		super();
		this.userId = userId;
		this.questId = questId;
		this.isRedeemed = isRedeemed;
		this.isComplete = isComplete;
	}

	/*
	 * public String getId(){return id;} public void setId(String id){this.id =
	 * id;}
	 */

	public Long getVersion() {
		return version;
	}

	public void setVersion(Long version) {
		this.version = version;
	}

	public String getUserId() {
		return userId;
	}

	public void setUserId(String userId) {
		this.userId = userId;
	}

	public int getQuestId() {
		return questId;
	}

	public void setQuestId(int questId) {
		this.questId = questId;
	}

	public boolean isRedeemed() {
		return isRedeemed;
	}

	public void setRedeemed(boolean isRedeemed) {
		this.isRedeemed = isRedeemed;
	}

	public boolean isComplete() {
		return isComplete;
	}

	public void setComplete(boolean isComplete) {
		this.isComplete = isComplete;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((userId == null) ? 0 : userId.hashCode());
		return result; 
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		QuestForUser other = (QuestForUser) obj;
		if (userId == null) {
			if (other.userId != null)
				return false;
		} else if (!userId.equals(other.userId))
			return false;
		return true;
	}

	@Override
	public String toString() {
		return "QuestForUser [version=" + version + ", userId=" + userId
				+ ", questId=" + questId + ", isRedeemed=" + isRedeemed
				+ ", isComplete=" + isComplete + "]";
	}

}