CREATE TABLE IF NOT EXISTS CLUSTER_STATUS( APPLICATION_ID  VARCHAR(128) NOT NULL PRIMARY KEY ,STATUS    VARCHAR(10) DEFAULT 'RUNNING' NOT NULL ,CURRENT_MASTER  VARCHAR(128) ,LAST_PROCESSED  TIMESTAMP ,PENDING_USURPER VARCHAR(128) ,USURP_TIMESTAMP TIMESTAMP );

CREATE TABLE IF NOT EXISTS QUEUED (ID BIGINT AUTO_INCREMENT NOT NULL PRIMARY KEY ,ENTITY_ID VARCHAR(128) NOT NULL ,PROCESS_ID VARCHAR(128) NOT NULL ,TS TIMESTAMP ,MESSAGE BLOB );